import React, { useEffect, useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Wordcloud from './wordcloud';
import { User, PubRepo,PubPriRepo,Commit } from '../types';

export default function UserPublic() {
  const languageCounts: { [key: string]: number } = {};

  const [username, setUsername] = useState<string>('');
  const [reponame, setReponame] = useState<string>('');
  //1. information about the user
  const [user, setUser] = useState<User>({
    login: '',
    html_url: '',
    name: null,
    company: null,
    location: null,
    bio: null,
    public_repos: 0,
    followers: 0,
    following: 0,
    created_at: '',
    updated_at: '',
    email: '', 
    hireable: null
  });
  //2. the public repos of a user
  const [publicrepos, setPublicrepos] = useState<PubRepo[]>([]);
  ////Since repos is an array and it's initialized as an empty array [], it doesn't require default values.
//However, for the user state, which is an object, providing initial default values is necessary to prevent potential runtime errors when accessing its properties.


  //1. information about the user
  const fetchUserData = async () => {
    try{
      const responseUser=await fetch (`https://api.github.com/users/${username}`);
      const dataUser=await responseUser.json();
      console.log(dataUser);
      setUser(dataUser);
    }catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  //2. the public repos of a user
  const fetchPublicRepoData = async () => {
    try {
      const responsePublic = await fetch(`https://api.github.com/users/${username}/repos`);
      const dataPublic = await responsePublic.json();
      console.log(dataPublic);
      setPublicrepos(dataPublic);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  //button to retrieve data from the api
  const handleButtonClick = () => {
    if (username.trim() !== '') {
      fetchUserData();
      fetchPublicRepoData();
      fetchPublicPrivateData();
    } else {
      alert('Please enter a GitHub username.');
    }
  };

  return (
    <div>
      <h1>GitHub Repositories for {username}</h1>
      <TextField
        label="Enter GitHub Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Button variant="contained" onClick={handleButtonClick}>
        Fetch Repositories
      </Button>
      <TextField
        label="Enter GitHub Reponame"
        value={reponame}
        onChange={(e) => setReponame(e.target.value)}
      />

      <ul>
        <li>Login: {user.login}</li>
        <li>Name: {user.name || 'N/A'}</li>
        <li>Profile URL: <a href={user.html_url}>{user.html_url}</a></li>
        <li>Company: {user.company || 'N/A'}</li>
        <li>Location: {user.location || 'N/A'}</li>
        <li>Bio: {user.bio || 'N/A'}</li>
        <li>Public Repositories: {user.public_repos}</li>
        <li>Followers: {user.followers}</li>
        <li>Following: {user.following}</li>
        <li>Joined GitHub: {user.created_at}</li>
        <li>Last Updated: {user.updated_at}</li>
        <li>Email: {user.email || 'N/A'}</li>
        <li>Hireable: {user.hireable ? 'Yes' : 'No'}</li>
      </ul>

      <ul>
      {publicrepos.slice(0, 2).map((publicrepo) => {
        return (
          <li key={publicrepo.name}>
            <h3>{publicrepo.name}</h3>
            <p>Full Name: {publicrepo.full_name}</p>
            <p>Private: {publicrepo.private.toString()}</p>
            <p>HTML URL: {publicrepo.html_url}</p>
            <p>Description: {publicrepo.description || 'N/A'}</p>
            <p>Fork: {publicrepo.fork.toString()}</p>
            <p>URL: {publicrepo.url}</p>
            <p>Created At: {publicrepo.created_at}</p>
            <p>Updated At: {publicrepo.updated_at}</p>
            <p>Pushed At: {publicrepo.pushed_at}</p>
            <p>Homepage: {publicrepo.homepage || 'N/A'}</p>
            <p>Size: {publicrepo.size}</p>
            <p>Stargazers Count: {publicrepo.stargazers_count}</p>
            <p>Watchers Count: {publicrepo.watchers_count}</p>
            <p>Language: {publicrepo.language || 'N/A'}</p>
            <p>Has Issues: {publicrepo.has_issues.toString()}</p>
            <p>Has Projects: {publicrepo.has_projects.toString()}</p>
            <p>Has Downloads: {publicrepo.has_downloads.toString()}</p>
            <p>Has Wiki: {publicrepo.has_wiki.toString()}</p>
            <p>Has Pages: {publicrepo.has_pages.toString()}</p>
            <p>Has Discussions: {publicrepo.has_discussions.toString()}</p>
            <p>Forks Count: {publicrepo.forks_count}</p>
            <p>Mirror URL: {publicrepo.mirror_url || 'N/A'}</p>
            <p>Archived: {publicrepo.archived.toString()}</p>
            <p>Disabled: {publicrepo.disabled.toString()}</p>
            <p>Open Issues Count: {publicrepo.open_issues_count}</p>
            <p>License: {publicrepo.license || 'N/A'}</p>
            <p>Web Commit Signoff Required: {publicrepo.web_commit_signoff_required.toString()}</p>
            <p>Topics: {publicrepo.topics.join(', ')}</p>
            <p>Visibility: {publicrepo.visibility}</p>
            <p>Forks: {publicrepo.forks}</p>
            <p>Open Issues: {publicrepo.open_issues}</p>
            <p>Watchers: {publicrepo.watchers}</p>
            <p>Default Branch: {publicrepo.default_branch}</p>
          </li>
        );
      })}
    </ul>
    </div>
  );
};
