import React, { useEffect, useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import { User, PubRepo,PubPriRepo,Commit } from '../types';

export default function UserPublic() {
  const languageCounts: { [key: string]: number } = {};
  const [username, setUsername] = useState<string>('');
  const [reponame, setReponame] = useState<string>('');

  //set the state/the initial info of the public repos and the user
  const [user, setUser] = useState<User>({
    login: '',
    html_url: '',
    name: null,
    company: null,
    location: null,
    bio: null,
    public_repos: 0,
    followers: 0,
    following: 0,
    created_at: '',
    updated_at: '',
    email: '', 
    hireable: null
  });
  const [publicrepos, setPublicrepos] = useState<PubRepo[]>([]);
  const [userDataJSON, setUserDataJSON] = useState<string>(''); // State for original dataUser JSON
  const [combinedInfoJSON, setCombinedInfoJSON] = useState<string>(''); // State for combinedInfo JSON


  //make api calls to retrieve information about the user and the repo
  const fetchUserData = async () => {
    try{
      const responseUser=await fetch (`https://api.github.com/users/${username}`);
      const dataUser=await responseUser.json();

      console.log("Original dataUser:", dataUser); //console.log the original information
      setUser(dataUser);//update the state with the new info
      setUserDataJSON(JSON.stringify(dataUser));
      const filteredUser = {
        login: dataUser.login,
        html_url: dataUser.html_url,
        name: dataUser.name,
        company: dataUser.company,
        location: dataUser.location,
        bio: dataUser.bio,
        public_repos: dataUser.public_repos,
        followers: dataUser.followers,
        following: dataUser.following,
        created_at: dataUser.created_at,
        updated_at: dataUser.updated_at,
        email: dataUser.email,
        hireable: dataUser.hireable
      };

      const timeOnGitHub = calculateTimeOnGitHub(dataUser.created_at);
      const activeLevel = calculateActiveLevel(dataUser.updated_at);
      const userValueToGitHub = calculateUserValueToGitHub(dataUser.followers, dataUser.public_repos);

      console.log("Time on GitHub:", timeOnGitHub);
      console.log("Active level:", activeLevel);
      console.log("user_value:",userValueToGitHub);
      const additionalInfo = {
        timeOnGitHub: timeOnGitHub,
        activeLevel: activeLevel,
        userValueToGitHub: userValueToGitHub
      };

      console.log("Additional info:", additionalInfo);
      const combinedInfo = {
        ...filteredUser,
        ...additionalInfo
      };
      setCombinedInfoJSON(JSON.stringify(combinedInfo));
      console.log("Combined info:", combinedInfo);
      return JSON.stringify(combinedInfo);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // Calculate time_on_github based on user's creation date
  const calculateTimeOnGitHub = (createdAt: string): string => {
    const currentDate = new Date();
    const createdDate = new Date(createdAt);
    const timeDiff = currentDate.getTime() - createdDate.getTime();
    const daysOnGitHub = Math.floor(timeDiff / (1000 * 3600 * 24));
    return `${daysOnGitHub} days`;
  };

  // Calculate active_level based on last updated date
  const calculateActiveLevel = (updatedAt: string): string => {
    const currentDate = new Date();
    const updatedDate = new Date(updatedAt);
    const timeDiff = currentDate.getTime() - updatedDate.getTime();
    const daysSinceLastUpdate = Math.floor(timeDiff / (1000 * 3600 * 24));

    if (daysSinceLastUpdate <= 30) {
      return "Very Active";
    } else if (daysSinceLastUpdate <= 90) {
      return "Relatively Active";
    } else if (daysSinceLastUpdate <= 365) {
      return "Not Very Active";
    } else {
      return "Not Active";
    }
  };

  const calculateUserValueToGitHub = (followers: number, publicRepos: number): { category: string, value: number } => {
    const userValue = followers + publicRepos;
    const thresholdGood = 100; 

    let category = "";
    if (userValue < thresholdGood) {
      category = "Relatively good user value";
    } else if (userValue < 2 * thresholdGood) {
      category = "Okay user value";
    } else {
      category = "Great user value";
    }

    return { category, value: userValue };
  };

  //2. the public repos of a user
  const fetchPublicRepoData = async () => {
    try {
      const responsePublic = await fetch(`https://api.github.com/users/${username}/repos`);
      const dataPublic = await responsePublic.json();
      console.log(dataPublic);//console.log the original information
      setPublicrepos(dataPublic);//update the state with the new info

    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  //button to retrieve data from the api
  const handleButtonClick = () => {
    if (username.trim() !== '') {
      fetchUserData();
      fetchPublicRepoData();
    } else {
      alert('Please enter a GitHub username.');
    }
  };

  //display the information retrieved from the api
  return (
    <div>
      <h1>GitHub Repositories for {username}</h1>
      <TextField
        label="Enter GitHub Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Button variant="contained" onClick={handleButtonClick}>
        Fetch Repositories
      </Button>

      {/* Display original dataUser JSON */}
      <div>
        <h2>Original Data User JSON:</h2>
        <pre>{userDataJSON}</pre>
      </div>

      {/* Display combined info JSON */}
      <div>
        <h2>Combined Info JSON:</h2>
        <pre>{combinedInfoJSON}</pre>
      </div>

      <ul>
        <li>Login: {user.login}</li>
        <li>Name: {user.name || 'N/A'}</li>
        <li>Profile URL: <a href={user.html_url}>{user.html_url}</a></li>
        <li>Company: {user.company || 'N/A'}</li>
        <li>Location: {user.location || 'N/A'}</li>
        <li>Bio: {user.bio || 'N/A'}</li>
        <li>Public Repositories: {user.public_repos}</li>
        <li>Followers: {user.followers}</li>
