import React, { useEffect, useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Wordcloud from './wordcloud';
import { User, Repo } from '../types';

export default function Github() {
  const [username, setUsername] = useState<string>('');
  const [reponame, setReponame] = useState<string>('');
  //1. information about the user
  const [user, setUser] = useState<User>({
    login: '',
    html_url: '',
    name: null,
    company: null,
    location: null,
    bio: null,
    public_repos: 0,
    followers: 0,
    following: 0,
    created_at: '',
    updated_at: '',
    email: '', 
    hireable: null
  });
  //2. the public repos of a user
  const [repos, setRepos] = useState<Repo[]>([]);
  ////Since repos is an array and it's initialized as an empty array [], it doesn't require default values.
//However, for the user state, which is an object, providing initial default values is necessary to prevent potential runtime errors when accessing its properties.

  //1. information about the user
  const fetchUserData = async () => {
    try{
      const responseUser=await fetch (`https://api.github.com/users/${username}`);
      const dataUser=await responseUser.json();
      setUser(dataUser);
    }catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  //2. the public repos of a user
  const fetchRepoData = async () => {
    try {
      const responseRepo = await fetch(`https://api.github.com/users/${username}/repos`);
      const dataRepo = await responseRepo.json();
      setRepos(dataRepo);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };


  //3. public and private repos of a user
  const fetchPublicPrivateData = async () => {
    try {
      const responsePublicPrivate= await fetch("https://api.github.com/user/repos", {
        headers: {
          Authorization: "Bearer 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"
        }
      });
      const dataPublicPrivate= await responsePublicPrivate.json();
      console.log(dataPublicPrivate);
      //add the frontend data structure
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  //4. the commits in a particular repo
  const fetchCommits = async () => {
    try {
      const responseCommits= await fetch(`https://api.github.com/repos/${username}/${reponame}/commits`, {
        headers: {
          Authorization: "Bearer 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"
        }
      });
      const dataCommits= await responseCommits.json();
      console.log(dataCommits);
      //add the frontend data types
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleButtonClick = () => {
    if (username.trim() !== '') {
      fetchRepoData();
      fetchUserData();
      fetchPublicPrivateData();
    } else {
      alert('Please enter a GitHub username.');
    }
  };

  const handleButtonClick2 = () => {
    if (reponame.trim() !== '') {
      fetchCommits();
    } else {
      alert('Please enter a GitHub username.');
    }
  };

  const languageCounts: { [key: string]: number } = {};

  return (
    <div>
      <h1>GitHub Repositories for {username}</h1>
      <TextField
        label="Enter GitHub Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Button variant="contained" onClick={handleButtonClick}>
        Fetch Repositories
      </Button>
      <TextField
        label="Enter GitHub Reponame"
        value={reponame}
        onChange={(e) => setReponame(e.target.value)}
      />
      <Button variant="contained" onClick={handleButtonClick2}>
        Fetch repo under this user
      </Button>
      <Wordcloud userData={user} userLanguage ={languageCounts}/>

      <ul>
        <li>{user.login}</li>
        <li>{user.name}</li>
        <li>{user.html_url}</li>
      </ul>
      <ul>
        {repos.slice(0, 15).map((repo) => {
          // Define and update languageMap within the map function
            const language = repo.language || 'N/A'; // Get the language or set to 'N/A' if undefined
            // Increment count for the language or initialize to 1 if not exists
            languageCounts[language] = (languageCounts[language] || 0) + 1;

          return (
            <li key={repo.name}>
              <h3>{repo.name}</h3>
              <p>Description: {repo.description || 'N/A'}</p>
              <p>Main Language: {repo.language || 'N/A'}</p>
              <p>Forks: {repo.forks_count}</p>
              <p>Stars: {repo.stargazers_count}</p>
              <p>Created At: {repo.created_at}</p>
              <p>Last Updated At: {repo.updated_at}</p>
            </li>
          );
        })}
      </ul>

    </div>
  );



};
