import React from 'react';

// The GraphQL query
const query = `
{
  user(login: "EveWangUW") {
    name
    email
    bio
    createdAt
    updatedAt
    repositories(first: 5) {
      nodes {
        name
        description
        createdAt
        updatedAt
      }
    }
  }
}`;


// The endpoint URL
const url = 'https://api.github.com/graphql';

const GraphQL: React.FC = () => {
  const fetchRepositories = () => {
    // The headers for the request
    const headers = {
      'Authorization': `Bearer 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`,
      'Content-Type': 'application/json'
    };

    // The request payload
    const payload = {
      query: query
    };

    // Make the POST request
    fetch(url, {
      method: 'POST',
      headers: headers,
      body: JSON.stringify(payload)
    })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        // Trigger the download
        downloadJSON(data, 'repositories_data.json');
      })
      .catch(error => {
        console.error('Error:', error);
      });
  };

  const downloadJSON = (json: object, filename: string) => {
    const blob = new Blob([JSON.stringify(json, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <div>
      <button onClick={fetchRepositories}>Fetch Repositories</button>
    </div>
  );
};

export default GraphQL;
