import React, { useEffect, useState } from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';

type Repo = {
  name: string;
  description: string | null;
  language: string | null;
  forks_count: number;
  stargazers_count: number;
  created_at: string;
  updated_at: string;
};

export default function Github() {
  const [username, setUsername] = useState<string>('');
  const [repos, setRepos] = useState<Repo[]>([]);

  const fetchUserData = async () => {
    try {
      const response = await fetch(`http://localhost:8080/${username}/repos`);
      const data = await response.json();
      setRepos(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleButtonClick = () => {
    if (username.trim() !== '') {
      fetchUserData();
    } else {
      alert('Please enter a GitHub username.');
    }
  };

  return (

    <div>
      <TextField
        label="Enter GitHub Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Button variant="contained" onClick={handleButtonClick}>
        Fetch Repositories
      </Button>
      <h1>GitHub Repositories for {username}</h1>
      <ul>
        {repos.slice(0, 15).map((repo) => (
          <li key={repo.name}>
            <h3>{repo.name}</h3>
            <p>Description: {repo.description || 'N/A'}</p>
            <p>Main Language: {repo.language || 'N/A'}</p>
            <p>Forks: {repo.forks_count}</p>
            <p>Stars: {repo.stargazers_count}</p>
            <p>Created At: {repo.created_at}</p>
            <p>Last Updated At: {repo.updated_at}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};
