import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import { fetchUserData, fetchPublicRepoData } from '../functions/api';
// import { calculateActiveLevel, calculateTimeOnGitHub, calculateUserValueToGitHub } from '../functions/calculations';
import UserInfo from './UserInfo';
import RepoList from './RepoList';
import { User, PubRepo } from '../types';
import GitHubRepos from './graphql';

const theme = createTheme({
  palette: {
    primary: {
      main: '#2196f3', // light blue
    },
    secondary: {
      main: '#f50057', // pink for secondary
    },
  },
});

export default function GitHub() {
  const [username, setUsername] = useState<string>('');
  const [user, setUser] = useState<User>({
    login: '',
    html_url: '',
    name: null,
    company: null,
    location: null,
    bio: null,
    public_repos: 0,
    followers: 0,
    following: 0,
    created_at: '',
    updated_at: '',
    email: '', 
    hireable: null
  });
  const [publicrepos, setPublicrepos] = useState<PubRepo[]>([]);
  const [userDataJSON, setUserDataJSON] = useState<string>('');
  const [combinedInfoJSON, setCombinedInfoJSON] = useState<string>('');

  const [timeOnGitHub, setTimeOnGitHub] = useState<string>('');
  const [activeLevel, setActiveLevel] = useState<string>('');
  const [userValueToGitHub, setUserValueToGitHub] = useState<{ category: string, value: number } | null>(null);

  //time of the user on github
const calculateTimeOnGitHub = (createdAt: string): string => {
  const currentDate = new Date();
  const createdDate = new Date(createdAt);
  const timeDiff = currentDate.getTime() - createdDate.getTime();
  const daysOnGitHub = Math.floor(timeDiff / (1000 * 3600 * 24));
  return `${daysOnGitHub} days`;
};

//the active level of the user based on the last updated time and current time
const calculateActiveLevel = (updatedAt: string): string => {
  const currentDate = new Date();
  const updatedDate = new Date(updatedAt);
  const timeDiff = currentDate.getTime() - updatedDate.getTime();
  const daysSinceLastUpdate = Math.floor(timeDiff / (1000 * 3600 * 24));

  if (daysSinceLastUpdate <= 30) {
    return "Very Active";
  } else if (daysSinceLastUpdate <= 90) {
    return "Relatively Active";
  } else if (daysSinceLastUpdate <= 365) {
    return "Not Very Active";
  } else {
    return "Not Active";
  }
};

//calcualte the user's programming skill level based on number of followers and number of public repos with different importance in weight
const calculateUserValueToGitHub = (followers: number, publicRepos: number): { category: string, value: number } => {
  // Define weights for followers and public repos
  const weightFollowers = 0.6;
  const weightPublicRepos = 0.4;

  // Calculate the weighted value
  const userValue = (followers * weightFollowers) + (publicRepos * weightPublicRepos);

  // Define threshold for categories
  const thresholdGood = 100;

  let category = "";
  if (userValue < thresholdGood) {
    category = "Relatively good user value";
  } else if (userValue < 2 * thresholdGood) {
    category = "Okay user value";
  } else {
    category = "Great user value";
  }

  return { category, value: userValue };
};

const GenerateAnalysis = async () => {
    //fetch the user's info with the username
    if (username.trim() !== '') {
      const dataUser = await fetchUserData(username);
      if (dataUser) {
        setUser(dataUser);
        setUserDataJSON(JSON.stringify(dataUser, null, 2));

        //retrieve the data fileds we need from the original raw json data
        const filteredUser = {
          login: dataUser.login,
          html_url: dataUser.html_url,
          name: dataUser.name,
          company: dataUser.company,
          location: dataUser.location,
          bio: dataUser.bio,
          public_repos: dataUser.public_repos,
          followers: dataUser.followers,
          following: dataUser.following,
          created_at: dataUser.created_at,
          updated_at: dataUser.updated_at,
          email: dataUser.email,
          hireable: dataUser.hireable
        };

        //provide input values for the data manipulation, and generate new data insights
        const additionalInfo = {
          timeOnGitHub: calculateTimeOnGitHub(dataUser.created_at),
          activeLevel: calculateActiveLevel(dataUser.updated_at),
          userValueToGitHub: calculateUserValueToGitHub(dataUser.followers, dataUser.public_repos)
        };

        //update the state with the newly generated data
        setTimeOnGitHub(additionalInfo.timeOnGitHub);
        setActiveLevel(additionalInfo.activeLevel);
        setUserValueToGitHub(additionalInfo.userValueToGitHub);

        //combine the filtered data and the newly generated data
        const combinedInfo = {
          ...filteredUser,
          ...additionalInfo
        };

        //update the state for the new information structure
        setCombinedInfoJSON(JSON.stringify(combinedInfo, null, 2));
      }

      const dataPublic = await fetchPublicRepoData(username);
      if (dataPublic) {
        setPublicrepos(dataPublic);
      }
    } else {
      alert('Please enter a GitHub username.');
    }
  };

  //set the download button for the user info and the user repo info
  const downloadJSON = (json: string, filename: string) => {
    const blob = new Blob([json], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="md">
        <Paper elevation={3} style={{ padding: '16px', marginTop: '16px' }}>
          <Box display="flex" flexDirection="column" alignItems="center">
            <GitHubRepos/>
            <Typography variant="h4" gutterBottom>
              GitHub Repositories for {username || '...'}
            </Typography>
            <TextField
              label="Enter GitHub Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              variant="outlined"
              fullWidth
              margin="normal"
            />
            <Grid container spacing={2} justifyContent="center" style={{ marginBottom: '16px' }}>
              <Grid item>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={GenerateAnalysis}
                >
                  Fetch user and repository information!
                </Button>
              </Grid>
            </Grid>
            <Grid container spacing={2} justifyContent="center" style={{ marginBottom: '16px' }}>
              <Grid item>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => downloadJSON(userDataJSON, `${username}_data.json`)}
                  disabled={!userDataJSON}
                >
                  Download User Data
                </Button>
              </Grid>
              <Grid item>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => downloadJSON(JSON.stringify(publicrepos, null, 2), `${username}_repos.json`)}
                  disabled={publicrepos.length === 0}
                >
                  Download Repositories Data
                </Button>
              </Grid>
            </Grid>
            <Box width="100%">
              <Typography variant="h6" gutterBottom>
                Original Data User JSON:
              </Typography>
              <pre style={{ whiteSpace: 'pre-wrap', wordWrap: 'break-word', backgroundColor: '#f5f5f5', padding: '10px', borderRadius: '4px' }}>
                {userDataJSON}
              </pre>
            </Box>
            <Box width="100%">
              <Typography variant="h6" gutterBottom>
                Combined Info JSON:
              </Typography>
              <pre style={{ whiteSpace: 'pre-wrap', wordWrap: 'break-word', backgroundColor: '#f5f5f5', padding: '10px', borderRadius: '4px' }}>
                {combinedInfoJSON}
              </pre>
            </Box>
            <UserInfo user={user} timeOnGitHub={timeOnGitHub} activeLevel={activeLevel} userValueToGitHub={userValueToGitHub} />
            <RepoList publicrepos={publicrepos} />
          </Box>
        </Paper>
      </Container>
    </ThemeProvider>
  );
}
